mapper.select:
SELECT BOARD_NUM, BOARD_TITLE, BOARD_CONTENT, USER_NUM, WARNING_COUNT,BOARD_FIELD,BOARD_REGISTER_DATE, BOARD_UPDATE_DATE
FROM TBL_BOARD
WHERE BOARD_NUM = #{boardNum}

SELECT WARNING_COUNT
FROM TBL_BOARD
WHERE BOARD_NUM = #{boardNum}

SELECT USER_ID
FROM TBL_USER
WHERE USER_NUM = #{userNum}
