mapper.select:
SELECT ANSWER_NUM, ANSWER_TITLE, ANSWER_CONTENT, ANSWER_SEND_DATE, USER_NUM, MESSAGE_NUM
FROM TBL_ANSWER
WHERE ANSWER_NUM = #{answerNum}

mapper.update:
UPDATE TBL_ANSWER
SET ANSWER_TITLE=#{answerTitle}, ANSWER_CONTENT=#{answerContent}, ANSWER_SEND_DATE=SYSDATE
WHERE ANSWER_NUM=#{answerNum}

mapper.select:
