mapper.update:
UPDATE TBL_MESSAGE
SET MESSAGE_TITLE=#{messageTitle}, MESSAGE_CONTENT=#{messageContent}, MESSAGE_SEND_DATE=SYSDATE
WHERE MESSAGE_NUM=#{messageNum}

mapper.select:
SELECT MESSAGE_SEND_DATE
FROM TBL_MESSAGE
WHERE MESSAGE_NUM = #{messageNum}

SELECT MESSAGE_NUM, MESSAGE_TITLE, MESSAGE_CONTENT, MESSAGE_SEND_DATE, USER_NUM
FROM TBL_MESSAGE
WHERE MESSAGE_NUM=#{messageNum}

SELECT MESSAGE_TITLE
FROM TBL_MESSAGE
WHERE MESSAGE_NUM = #{messageNum}

SELECT MESSAGE_CONTENT
FROM TBL_MESSAGE
WHERE MESSAGE_NUM = #{messageNum}
